INSERT INTO `wp_posts` VALUES("2", "1", "2019-02-06 14:01:11", "2019-02-06 11:01:11", "<!-- wp:columns -->\n<div class=\"wp-block-columns has-2-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":21} -->\n<figure class=\"wp-block-image\"><img src=\"http://boncraft/wp-content/uploads/2019/02/1-min-1.png\" alt=\"\" class=\"wp-image-21\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"level\":3} -->\n<h3>Транспортная компания - Бонкрафт Логистика </h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>\n\nТранспортная компания «Бонкрафт Логистика» предлагает услуги грузоперевозок собственным транспортом Удмуртии и всей России. Пунктуальность, уважение к каждому клиенту, четкое следование условиям договора перевозки, контроль сохранности груза – вот основные правила, которые гарантируют высокий сервис, безопасность и своевременность доставки.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Мы работаем на рынке логистических услуг более 8 лет, что позволило расширить возможности в сфере грузоперевозок, наработать клиентскую базу, завоевать доверие постоянных заказчиков. Наш автопарк дает возможность обеспечить транспортировку небольших и крупногабаритных грузов. Наши сотрудники готовы предложить помощь в оформлении заявки на любой маршрут.\n\n</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:paragraph -->\n<p>вапролджэ</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:editor-blocks/wrapper {\"paddingTop\":0,\"paddingBottom\":0,\"marginTop\":0,\"marginBottom\":0,\"backgroundImage\":\"http://boncraft/wp-content/uploads/2019/02/image-min.jpg\"} -->\n<div style=\"background-image:url(http://boncraft/wp-content/uploads/2019/02/image-min.jpg)\" class=\"wp-block-editor-blocks-wrapper alignfull\"><div class=\"wrapper-inner\"><div class=\"wrapper-inner-blocks\"><!-- wp:heading -->\n<h2>Услуги компании</h2>\n<!-- /wp:heading 