INSERT INTO `wp_posts` VALUES("1706", "1", "2019-02-12 22:20:37", "2019-02-12 19:20:37", "<!-- wp:columns -->\n<div class=\"wp-block-columns has-2-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph -->\n<p>\n\n«Бонкрафт-Логистика» - профессиональная транспортно-экспедиционная компания России, на рынке уже более 8 лет. Обширная транспортная система, гибкие, конкурентоспособные цены, высочайший уровень обслуживания клиентов, первоклассные специалисты и многолетний опыт позволяют нам занимать лидирующие позиции на рынке грузоперевозок в Ижевске и по России.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Мы предоставляем перевозку любых, в том числе негабаритных, грузов на любые расстояния в любом направлении, без ограничения по весу и объему. Все наши услуги предоставляются по системе как полным пакетом, так и по отдельности, в зависимости от пожеланий клиента.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>К Вашим услугам: собственный автопарк из автомашин МАН, МЕРСЕДЕС, СКАНИЯ, ВОЛЬВО и прицепов к ним различных марок, осуществляющие автомобильные перевозки.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Консультация по любому вопросу: 8 (3412) 970 401.\n\n</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":1707} -->\n<figure class=\"wp-block-image\"><img src=\"http://boncraft/wp-content/uploads/2019/02/Mask-Group.png\" alt=\"\" class=\"wp-image-1707\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->", "О компании", "", "publish", "closed", "closed", "", "o-kompanii", "", "", "2019-02-12 22:20:37", "2019-02-12 19:20:37", "", "0", "http://boncraft/?page_id=1706", "0", "page", "", "0");
